package uz.gita.appbuilderadmin.presentation.componentsimport android.os.Buildimport androidx.annotation.RequiresApiimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport uz.gita.appbuilderadmin.data.model.ComponentsModel@RequiresApi(Build.VERSION_CODES.O)@Composablefun     RowComponent(    componentsModel: List<ComponentsModel>,) {    Row(modifier = Modifier.fillMaxWidth()) {        componentsModel.forEach { component ->            when (component.componentsName) {                "Input" -> {                    RowInputComponent(data = component, modifier = Modifier.weight(1f))                }                "Text" -> {                    RowTextComponent(data = component, modifier = Modifier.weight(1f))                }                "Selector" -> {                    RowSelectorComponent(data = component, modifier = Modifier.weight(1f))                }                "MultiSelector" -> {                    RowMultiSelectorComponent(                        question = component.multiSelectDataQuestion,                        list = component.multiSelectorDataAnswers,                        onLongClick = { },                        onClickDelete = { },                        data = component,                        modifier = Modifier.weight(1f)                    )                }                "Date Picker" -> {                    RowDateComponent(                        modifier = Modifier.weight(1f), date = "", onLongClick = {},                        listener = { }, dataaa = component                    )                }            }        }    }}